"use client";
import React from "react";
import { motion } from "framer-motion";
import { useRouter } from "next/navigation";
import { isInternalJob, type Job } from "./JobTable";
import dayjs from "dayjs";
import relativeTime from "dayjs/plugin/relativeTime.js";

interface JobCardProps {
  job: Job;
  jobIndex: number;
}

export const GoogleIcon = () => (
  <svg
    width="25"
    height="24"
    viewBox="0 0 25 24"
    fill="none"
    xmlns="http://www.w3.org/2000/svg"
    xmlnsXlink="http://www.w3.org/1999/xlink"
    className="w-5 h-5 flex-shrink-0"
  >
    <rect
      x="0.895874"
      width="23.4375"
      height="24"
      fill="url(#pattern0_2_528)"
    />
    <defs>
      <pattern
        id="pattern0_2_528"
        patternContentUnits="objectBoundingBox"
        width="1"
        height="1"
      >
        <use xlinkHref="#image0_2_528" transform="scale(0.004 0.00390625)" />
      </pattern>
      <image
        id="image0_2_528"
        width="250"
        height="256"
        preserveAspectRatio="none"
        xlinkHref="data:image/png;base64,"
      />
    </defs>
  </svg>
);
const JobCard: React.FC<JobCardProps> = ({ job, jobIndex }) => {
  const router = useRouter();
  const handleClick = () => {
    if (isInternalJob(job)) {
      router.push(`/job/internal/${job._id}`); // keep your existing internal logic
    } else {
      // IMPORTANT: external jobs must navigate with Mongo _id so refresh works
      // Ensure ExternalJob type includes _id from DB
      const extId = (job as Job)._id;
      router.push(`/job/external/${extId}`);
    }
  };
  dayjs.extend(relativeTime);
  const time = dayjs(job.createdAt).fromNow();
  return (
    <motion.div
      onClick={handleClick}
      initial={{ opacity: 0, x: -40 }}
      animate={{ opacity: 1, x: 0 }}
      transition={{ delay: jobIndex * 0.05, duration: 0.5 }}
      className="
    relative
    px-4 py-3
    w-full max-w-[440px]
    lg:h-[320px] h-[320px]
    font-outfit
    border border-[#000000]/10
    rounded-[30px]
    m-auto mb-6
    cursor-pointer
    hover:scale-105
    transition-transform
    overflow-hidden
    box-border
    bg-white
    flex flex-col justify-between
  "
    >
      {/* כותרת עליונה */}
      <div>
        <div className="flex justify-between items-center px-3 pt-3">
          <h2 className="text-base sm:text-lg font-semibold truncate max-w-[70%]">
            {job.job}
          </h2>
          <GoogleIcon />
        </div>

        {/* מיקום וזמן */}
        <div className="flex justify-between items-center px-3 mt-2 text-sm sm:text-base text-gray-600">
          <h3 className="truncate">
            {job.city}, {job.country}
          </h3>
          <h3 className="whitespace-nowrap">{time}</h3>
        </div>

        {/* תיאור */}
        <p className="line-clamp-3 mx-3 mt-4 mb-2 text-[#232323] text-xs sm:text-sm leading-relaxed">
          {job.description}
        </p>

        {/* תגיות */}
        <div className="grid grid-cols-3 gap-2 px-3 mb-4">
          <span className="bg-[#F9F9F9] rounded-[20px] text-center py-1 text-[10px] sm:text-xs truncate">
            Project Strategy
          </span>
          <span className="bg-[#F9F9F9] rounded-[20px] text-center py-1 text-[10px] sm:text-xs truncate">
            Cloud
          </span>
          <span className="bg-[#F9F9F9] rounded-[20px] text-center py-1 text-[10px] sm:text-xs truncate">
            AWS
          </span>
        </div>
        <div className="border-b-[1px] border-[#000000]/10"></div>
      </div>

      {/* כפתורים בתחתית */}
      <div className="grid grid-cols-2 gap-3 px-3 pb-4">
        <button className="font-outfit rounded-[14px] border border-[#000000] py-2 sm:py-3 px-2 sm:px-4 text-sm sm:text-base hover:bg-[#24A8A2] transition-colors">
          Learn More
        </button>
        <button className="font-outfit rounded-[14px] text-white bg-[#11AEFF] py-2 sm:py-3 px-2 sm:px-4 text-sm sm:text-base hover:bg-[#0d8ed6] transition-colors">
          Apply
        </button>
      </div>
    </motion.div>
  );
};

export default JobCard;